import React,{Component} from 'react';

class Like extends Component {
    constructor(props){
        super(props);
        this.state = {
            countLikes: parseInt(this.props.value),
        }
    }
    changeLikesValue = () => {
        this.setState((prevValue) => {
            return({
                countLikes: prevValue.countLikes + 1
            })
        })
    }
    render(){
        return(
            <div>
            <div>{this.state.countLikes} likes</div>
            <button onClick={this.changeLikesValue}><i className="fas fa-heart"></i></button>
            </div>
        )
    }
}

export default Like;
