import React,{Component} from 'react';
import './CounterFollow.css';


class Counter extends Component {
    constructor(props){
        super(props);
        this.state = {
            count: parseInt(this.props.initValue),
        }
    }
    changeCounterValue = () => {
        return this.setState({count: parseInt(this.props.initValue)+1})
    }
    render(){
        return(
            <div className="numberFolowers">
                {this.state.count} Follower(s)
                <button onClick={this.changeCounterValue}>Follow</button>
            </div>
        )
    }
}     
export default Counter;
