import React,{Component} from 'react';
import contentStyle from './Content.module.css';

class Newpost extends Component {
  constructor(props){
      super(props);
      this.state = {
          value: " ",
      }
  }
  createAPost(event){
    this.setState({
      value: event.target.value
    });
  }

  render(){
      return(
          <div className="createPost">
              <textarea value={this.state.value} placeholder="Add a post on my page"></textarea>
              <button onClick={this.state.createAPost} type="submit">Create new post</button>
          </div>
      )
  }
} 

export default Newpost;
